    LANGUAGE plpgsql SECURITY DEFINER
    AS $$
BEGIN
    RETURN QUERY
    SELECT 
        ss.setting_key,
        COALESCE(
            (SELECT bs.setting_value 
             FROM public.branch_settings bs 
             WHERE bs.setting_key = ss.setting_key 
             AND bs.branch_id = p_branch_id),
            ss.setting_value,
            ss.default_value
        ) as setting_value,
