-- Name: process_return_order(uuid, text); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.process_return_order(p_return_id uuid, p_processed_by text) RETURNS jsonb
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$
DECLARE
  v_return record;
  v_item record;
  v_old_stock numeric;
  v_new_stock numeric;
BEGIN
  SELECT * INTO v_return FROM public.return_orders WHERE return_id = p_return_id FOR UPDATE;
  IF NOT FOUND THEN
    RAISE EXCEPTION 'Return order not found';
  END IF;
  
  IF v_return.status != 'PENDING' THEN
    RAISE EXCEPTION 'Return order already processed';
  END IF;

  FOR v_item IN 
    SELECT * FROM public.return_order_items WHERE return_id = p_return_id 
  LOOP
    -- Get current stock
    SELECT current_stock INTO v_old_stock 
    FROM public.products 
    WHERE product_id = v_item.product_id;
    
    v_new_stock := v_old_stock - v_item.quantity;
    
    -- Check if we have enough stock
    IF v_new_stock < 0 THEN
      RAISE EXCEPTION 'Insufficient stock for product_id %. Current: %, Return: %', 
        v_item.product_id, v_old_stock, v_item.quantity;
    END IF;

    -- Create stock movement (OUT)
      product_id, movement_type, quantity, unit_cost, reference_type,
      reference_id, reference_code, reason, notes, created_by, 
      batch_id, supplier_id, old_stock, new_stock, created_at
    ) VALUES (
      v_item.product_id,
      'OUT',
      v_item.quantity,
      v_item.unit_cost,
      'RETURN_GOODS',
      NULL,
      v_return.return_code,
      'Trả hàng cho NCC: ' || v_return.reason,
      v_item.notes,
      p_processed_by,
      v_item.batch_number,
      v_return.supplier_id,
      v_old_stock,
      v_new_stock,
      now()
    );

    -- Update product stock
    UPDATE public.products 
    SET current_stock = v_new_stock, updated_at = now()
    WHERE product_id = v_item.product_id;
  END LOOP;

  -- Mark return as processed
  UPDATE public.return_orders 
  SET status = 'PROCESSED', processed_at = now(), processed_by = p_processed_by
  WHERE return_id = p_return_id;

  RETURN jsonb_build_object('status', 'PROCESSED', 'processed_at', now());
END;$$;


--
-- Name: receive_inbound_items(uuid, jsonb, text); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.receive_inbound_items(p_inbound_id uuid, p_lines jsonb, p_created_by text) RETURNS jsonb
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$
DECLARE
  v_line jsonb;
  v_item record;
  v_order record;
  v_status text;
  v_all_received boolean := true;
  v_any_received boolean := false;
  v_supplier_id int;
  v_code text;
  v_old_stock numeric;
  v_new_stock numeric;
BEGIN
  SELECT * INTO v_order FROM public.inbound_orders WHERE inbound_id = p_inbound_id FOR UPDATE;
  IF NOT FOUND THEN
    RAISE EXCEPTION 'Inbound order not found %', p_inbound_id;
  END IF;
  v_supplier_id := v_order.supplier_id;
  v_code := v_order.inbound_code;

  FOR v_line IN SELECT * FROM jsonb_array_elements(p_lines) LOOP
    SELECT * INTO v_item FROM public.inbound_order_items 
      WHERE item_id = (v_line->>'item_id')::uuid AND inbound_id = p_inbound_id FOR UPDATE;
    IF NOT FOUND THEN
      RAISE EXCEPTION 'Item not found %', v_line->>'item_id';
    END IF;

    IF (v_line->>'receive_qty')::numeric <= 0 THEN
      RAISE EXCEPTION 'Invalid receive qty for item %', v_line->>'item_id';
    END IF;
    IF v_item.received_qty + (v_line->>'receive_qty')::numeric > v_item.ordered_qty THEN
      RAISE EXCEPTION 'Receive qty exceeds ordered for item %', v_line->>'item_id';
    END IF;

    -- Get current stock before update
    SELECT current_stock INTO v_old_stock 
    FROM public.products 
    WHERE product_id = v_item.product_id;
    
    v_new_stock := v_old_stock + (v_line->>'receive_qty')::numeric;

    -- Update item
    UPDATE public.inbound_order_items
      SET received_qty = received_qty + (v_line->>'receive_qty')::numeric
    WHERE item_id = (v_line->>'item_id')::uuid;

    -- Create stock movement with old_stock
      product_id, movement_type, quantity, unit_cost, reference_type,
      reference_id, reference_code, reason, notes, created_by, 
      batch_id, supplier_id, old_stock, new_stock, created_at
    ) VALUES (
      v_item.product_id,
      'IN',
      (v_line->>'receive_qty')::numeric,
      v_item.unit_cost,
      'PURCHASE_ORDER',
      NULL,
      v_code,
      'Nhập theo ' || v_code,
      NULL,
      p_created_by,
      NULL,
      v_supplier_id,
      v_old_stock,
      v_new_stock,
      now()
    );

    -- Update product stock
    UPDATE public.products 
    SET 
      current_stock = v_new_stock,
      cost_price = v_item.unit_cost,
      updated_at = now()
    WHERE product_id = v_item.product_id;
  END LOOP;

  -- Determine status
  FOR v_item IN SELECT * FROM public.inbound_order_items WHERE inbound_id = p_inbound_id LOOP
    IF v_item.received_qty < v_item.ordered_qty THEN
      v_all_received := false;
    END IF;
    IF v_item.received_qty > 0 THEN
      v_any_received := true;
    END IF;
  END LOOP;

  IF v_all_received THEN
    v_status := 'RECEIVED';
    UPDATE public.inbound_orders SET status = v_status, received_date = now() WHERE inbound_id = p_inbound_id;
  ELSIF v_any_received THEN
    v_status := 'PARTIAL';
    UPDATE public.inbound_orders SET status = v_status WHERE inbound_id = p_inbound_id;
  ELSE
    v_status := 'PENDING';
  END IF;

  RETURN jsonb_build_object('status', v_status);
END;$$;


--
-- Name: record_stock_movement(integer, character varying, numeric, numeric, character varying, integer, character varying, text, text, character varying, character varying); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.record_stock_movement(p_product_id integer, p_movement_type character varying, p_quantity numeric, p_unit_cost numeric DEFAULT NULL::numeric, p_reference_type character varying DEFAULT 'MANUAL'::character varying, p_reference_id integer DEFAULT NULL::integer, p_reference_code character varying DEFAULT NULL::character varying, p_reason text DEFAULT 'System generated'::text, p_notes text DEFAULT NULL::text, p_created_by character varying DEFAULT 'System'::character varying, p_batch_id character varying DEFAULT NULL::character varying) RETURNS integer
    LANGUAGE plpgsql
    AS $$
DECLARE
    v_old_stock NUMERIC(15,2);
    v_new_stock NUMERIC(15,2);
    v_movement_id INTEGER;
BEGIN
    -- Get current stock
    SELECT current_stock INTO v_old_stock 
    FROM public.products 
    WHERE product_id = p_product_id;
    
    IF NOT FOUND THEN
        RAISE EXCEPTION 'Product not found: %', p_product_id;
    END IF;
    
    -- Calculate new stock
    CASE p_movement_type
        WHEN 'IN', 'FOUND' THEN 
            v_new_stock := v_old_stock + ABS(p_quantity);
        WHEN 'OUT', 'LOSS' THEN 
            v_new_stock := GREATEST(0, v_old_stock - ABS(p_quantity));
        WHEN 'ADJUST' THEN
            v_new_stock := GREATEST(0, v_old_stock + p_quantity);
        WHEN 'TRANSFER' THEN
            v_new_stock := GREATEST(0, v_old_stock - ABS(p_quantity));
        ELSE
            RAISE EXCEPTION 'Invalid movement type: %', p_movement_type;
    END CASE;
    
    -- Insert movement record
        product_id, movement_type, quantity, old_stock, new_stock,
        unit_cost, total_cost, reference_type, reference_id, reference_code,
        reason, notes, created_by, batch_id
    ) VALUES (
        p_product_id, p_movement_type, p_quantity, v_old_stock, v_new_stock,
        p_unit_cost, 
        CASE WHEN p_unit_cost IS NOT NULL THEN ABS(p_quantity) * p_unit_cost ELSE NULL END,
        p_reference_type, p_reference_id, p_reference_code,
        p_reason, p_notes, p_created_by, p_batch_id
    ) RETURNING movement_id INTO v_movement_id;
    
    -- Update product stock
    UPDATE public.products 
    SET current_stock = v_new_stock, updated_at = CURRENT_TIMESTAMP
    WHERE product_id = p_product_id;
    
    RETURN v_movement_id;
END;
$$;


