        WHEN 'OUT', 'LOSS' THEN v_new_stock := GREATEST(0, v_old_stock - ABS(p_quantity));
        WHEN 'ADJUST' THEN v_new_stock := GREATEST(0, v_old_stock + p_quantity);
        WHEN 'TRANSFER' THEN v_new_stock := GREATEST(0, v_old_stock - ABS(p_quantity));
        ELSE RAISE EXCEPTION 'Invalid movement type: %', p_movement_type;
    END CASE;

        product_id,
        supplier_id,
        movement_type,
        quantity,
        old_stock,
        new_stock,
        unit_cost,
        total_cost,
        reference_type,
        reference_id,
        reference_code,
        reason,
        notes,
        created_by,
        batch_id
    ) VALUES (
        p_product_id,
        p_supplier_id,
        p_movement_type,
        p_quantity,
        v_old_stock,
        v_new_stock,
        p_unit_cost,
        CASE WHEN p_unit_cost IS NOT NULL THEN ABS(p_quantity) * p_unit_cost ELSE NULL END,
        p_reference_type,
        p_reference_id,
        p_reference_code,
        p_reason,
        p_notes,
        p_created_by,
        p_batch_id
    ) RETURNING movement_id INTO v_movement_id;

    UPDATE public.products
    SET current_stock = v_new_stock,
        updated_at = CURRENT_TIMESTAMP
    WHERE product_id = p_product_id;

    RETURN v_movement_id;
END;
$$;


--
-- Name: record_stock_movement(integer, text, numeric, numeric, text, integer, text, text, text, text, uuid, integer, text, date); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.record_stock_movement(p_product_id integer, p_movement_type text, p_quantity numeric, p_unit_cost numeric DEFAULT NULL::numeric, p_reason text DEFAULT 'MANUAL'::text, p_invoice_id integer DEFAULT NULL::integer, p_reference_code text DEFAULT NULL::text, p_notes text DEFAULT NULL::text, p_location text DEFAULT NULL::text, p_created_by text DEFAULT 'System'::text, p_related_movement_id uuid DEFAULT NULL::uuid, p_supplier_id integer DEFAULT NULL::integer, p_batch_number text DEFAULT NULL::text, p_expiry_date date DEFAULT NULL::date) RETURNS uuid
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$
DECLARE
  v_movement_id uuid;
  v_new_stock numeric;
  v_cost_impact numeric;
BEGIN
  -- Insert movement record
    product_id, movement_type, quantity, unit_cost, reason,
    invoice_id, reference_code, notes, location, created_by, 
    related_movement_id, supplier_id, batch_number, expiry_date
  ) VALUES (
    p_product_id, p_movement_type, p_quantity, p_unit_cost, p_reason,
    p_invoice_id, p_reference_code, p_notes, p_location, p_created_by,
    p_related_movement_id, p_supplier_id, p_batch_number, p_expiry_date
  ) RETURNING movement_id INTO v_movement_id;

  -- Calculate stock impact
  CASE p_movement_type
    WHEN 'IN' THEN v_new_stock := p_quantity;
    WHEN 'OUT' THEN v_new_stock := -p_quantity;
    WHEN 'ADJUST' THEN v_new_stock := p_quantity;
    WHEN 'LOSS' THEN v_new_stock := -p_quantity;
    WHEN 'FOUND' THEN v_new_stock := p_quantity;
    ELSE v_new_stock := 0;
  END CASE;

  -- Calculate cost impact
  v_cost_impact := v_new_stock * COALESCE(p_unit_cost, 0);

  -- Update product stock
  UPDATE public.products 
  SET 
    current_stock = current_stock + v_new_stock,
    cost_price = CASE 
      WHEN p_unit_cost IS NOT NULL AND p_movement_type = 'IN' THEN p_unit_cost
      ELSE cost_price 
    END,
    updated_at = now()
  WHERE product_id = p_product_id;

  RETURN v_movement_id;
END;
$$;


