--
-- Name: stock_movements_detailed; Type: VIEW; Schema: public; Owner: -
--

CREATE VIEW public.stock_movements_detailed AS
 SELECT sm.movement_id,
    sm.product_id,
    sm.movement_type,
    sm.quantity,
    sm.old_stock,
    sm.new_stock,
    sm.unit_cost,
    sm.total_cost,
    sm.reference_type,
    sm.reference_id,
    sm.reference_code,
    sm.reason,
    sm.notes,
    sm.branch_id,
    sm.created_by,
    sm.created_at,
    sm.is_system_generated,
    sm.batch_id,
    sm.supplier_id,
    sm.batch_number,
    sm.expiry_date,
    p.product_name,
    p.product_code,
    s.supplier_name
   FROM ((public.stock_movements sm
     LEFT JOIN public.products p ON ((sm.product_id = p.product_id)))
     LEFT JOIN public.suppliers s ON ((sm.supplier_id = s.supplier_id)))
  ORDER BY sm.created_at DESC;


