--
-- Name: inbound_orders_summary _RETURN; Type: RULE; Schema: public; Owner: -
--

CREATE OR REPLACE VIEW public.inbound_orders_summary AS
 SELECT o.inbound_id,
    o.inbound_code,
    o.supplier_id,
    o.expected_date,
    o.received_date,
    o.status,
    o.notes,
    o.created_by,
    o.created_at,
    COALESCE(sum(i.ordered_qty), (0)::numeric) AS ordered_total_qty,
    COALESCE(sum(i.received_qty), (0)::numeric) AS received_total_qty,
    COALESCE(sum(i.total_cost), (0)::numeric) AS total_cost,
    s.supplier_name
   FROM ((public.inbound_orders o
     LEFT JOIN public.inbound_order_items i ON ((o.inbound_id = i.inbound_id)))
     LEFT JOIN public.suppliers s ON ((o.supplier_id = s.supplier_id)))
  GROUP BY o.inbound_id, s.supplier_name
  ORDER BY o.created_at DESC;


--
-- Name: return_orders_summary _RETURN; Type: RULE; Schema: public; Owner: -
--

CREATE OR REPLACE VIEW public.return_orders_summary AS
 SELECT ro.return_id,
    ro.return_code,
    ro.supplier_id,
    ro.inbound_id,
    ro.return_date,
    ro.reason,
    ro.status,
    ro.notes,
    ro.total_amount,
    ro.created_by,
    ro.created_at,
    ro.processed_at,
    ro.processed_by,
    s.supplier_name,
    io.inbound_code,
    COALESCE(sum(ri.quantity), (0)::numeric) AS total_quantity,
    count(ri.return_item_id) AS item_count
   FROM (((public.return_orders ro
     LEFT JOIN public.suppliers s ON ((ro.supplier_id = s.supplier_id)))
     LEFT JOIN public.inbound_orders io ON ((ro.inbound_id = io.inbound_id)))
     LEFT JOIN public.return_order_items ri ON ((ro.return_id = ri.return_id)))
  GROUP BY ro.return_id, s.supplier_name, io.inbound_code
  ORDER BY ro.created_at DESC;


